/**
 *
 * @param {number} a
 * @param {number} b
 * @returns {number} - a + b
 */
declare const sum: (a: number, b: number) => number;
/**
 *
 * @param {number} a
 * @param {number} b
 * @returns {number} - a - b
 */
declare const sub: (a: number, b: number) => number;
/**
 *
 * @param {number} a
 * @param {number} b
 * @returns {number} - a * b
 */
declare const mult: (a: number, b: number) => number;
/**
 *
 * @param {number} a
 * @param {number} b
 * @returns {number} - a / b
 */
declare const div: (a: number, b: number) => number;
export { sum, sub, mult, div };
